mapper.select:
select * from comment inner join user 
on comment.user_idx = user.user_idx 
where comment.community_idx = ${community_idx}

select * from comment inner join user 
on comment.user_idx = user.user_idx 
where comment.community_idx = ${community_idx}

mapper.update:
update user 

	nickname = #{nickname}, 
	password = #{password},
		update_date = #{update_date}

user_idx = #{user_idx}
